import React, { useContext, useState } from 'react';
import {Context} from '../index'
import styles from './styles/Pages.module.css'
import { observer } from 'mobx-react-lite';
import setPage from '../store/PartStore'

const Pages = observer (() => {
    const {part} = useContext(Context)
    const pageCount = Math.ceil(part.totalCount / part.limit) // 110 страниц округляем для получения количества страниц
    const pages = [] //массив с количеством страниц
    // const [listPagesQt, setListPagesQt] = useState(0)
    const qtPages = 8
    
    function allCountPages () {
        for (let i = 0; i < qtPages; i++ ) { //массив с цифрами страницы
            pages.push(i+1) // Номер страницы
        }
    }
    allCountPages() // Нам надо изменять массив pages[]
    
    console.log(pages);
    console.log(pageCount);
    

    return (
        <nav className={styles.pages__container}>
            <ul className={styles.pages__row_container}>
                    <li 
                        className={styles.pages__item}
                        >
                            {'<<'}
                    </li>
                    <li 
                        className={styles.pages__item}
                        >
                            {'<'}
                    </li>
                
                {pages.map(page => 
                    <li 
                        className={styles.pages__item}
                        key={page}
                        active={part.page === page}
                        onClick={() => 
                            part.setPage(page) 
                            }
                        >
                            {page}
                    </li>
                )}
                    <li 
                        className={styles.pages__item}
                        >
                            {'>'}
                    </li>
                    <li 
                        className={styles.pages__item}
                        >
                            {'>>'}
                    </li>
            </ul>
        </nav>
    );
});

export default Pages;
