import React from 'react';
import ButtonHeader from '../UI/button/ButtonHeader/ButtonHeader';
import styles from './DropDowm.module.css'

const DropDownMenu = (props) => {
    const {part} = useContext(Context)
    return (
        <div>
            <ButtonHeader name='Выберите категорию'/>
            <ul>
            <li className={styles.dropDown__container}>
                {props.text}
            </li>
            </ul>
        </div>
    );
};

export default DropDownMenu;
