import React, { useContext } from 'react';
import styles from './DropDowm.module.css'
import { Context } from '../../index.js';
import { observer } from 'mobx-react-lite';

const DropDownMenu = observer (() => {
    const {part} = useContext(Context)
    
    return (
        <div className={styles.dropdown}>
            <button className={styles.dropdown__button}>Выберите категорию</button>
            <ul className={styles.dropdown__items}>
            {part.categories.map(category => 
                   <li 
                    className={styles.categoryBar__list_item}
                    onClick={() => part.setSelectedCategory(category)}
                    key={category.id}
                    tabIndex= {0}
                    
                    >
                        {category.name}
                   </li> 
                )}
            {/* {part.categories.map(category => 
                    <li key={category.id}>{category.name}</li>
                )} */}
            </ul>
        </div>
        
    );
});

export default DropDownMenu;
